<script setup lang="ts">
import { ref, onMounted, onBeforeUnmount } from "vue";

// const activeApps = ref(["iTerm2", "Google Chrome", "MongoDB Compass", "WeChat", "VSCode", "NeteaseMusic", "Obsidian", "Knock", "wpsoffice", "System Settings", "Finder", "Chatbox", "Sublime Text"]);
const activeApps = ref([]);
const timeoutId = ref<NodeJS.Timeout>();
const activeIndex = ref<number>(0);

async function getAvtiveApp() {
    try {
        const res = await fetch(
            `https://blogapi.afunny.top/.netlify/functions/get-active-app`
        );
        const resJson = await res.json()
        activeApps.value = resJson.resultCountList
    } catch (error) {
        //
    } finally {
        timeoutId.value = setTimeout(() => {
            getAvtiveApp()
        }, 1000 * 10);
    }
}

onMounted(() => {
    getAvtiveApp()

    setInterval(() => {
        activeIndex.value = activeIndex.value + 1 >= activeApps.value.length ? 0 : activeIndex.value + 1
    }, 8000);
});

onBeforeUnmount(() => {
    if (timeoutId) {
        clearTimeout(timeoutId.value)
    }
})
</script>

