<style>
@keyframes slide {

    0% {
        opacity: 0;
    }

    100% {
        opacity: 1;
    }
}

.animate-slide {
    animation: slide 1s;
}

.breathing {
    animation: breath 3s infinite ease-in-out;
}

@keyframes breath {
    0% {
        opacity: 0.8;
        transform: scale(1);
    }

    50% {
        opacity: 1;
        transform: scale(1.2);
    }

    100% {
        opacity: 0.8;
        transform: scale(1);
    }
}
</style>
